import React, {Component} from "react";
import axios from "axios";
// import { response } from "express";

export default class Login extends Component {
    constructor(props) {
        super(props);

        this.state = {
            email: "",
            password: "",
            validation: {
                email: {
                    isValid: false
                },
                password: {
                    isComplete: false
                }
            }
        };

        this.handleSubmit = this.handleSubmit.bind(this);
        this.handleChange = this.handleChange.bind(this);

    }

    handleValidation() {
        // email
        // const email = document.querySelector('#login_user_email').value.trim()
        // const password = document.querySelector('#login_user_password').value.trim()

        // create a condition to check whether email is a valid email using regEx
        const regEmail = /^[^\s@]+@[^\s@]+\.[^\s@]+$/

        this.setState({
            validation: {
                email: {
                    isComplete: password !== "" ? true : false
                },
                // isComplete: email !== "" && regEmail.test(email) && password !== "" && password === confirmation ? true : false 
            }
        })
    }


    handleChange(event) {

        this.handleValidation()

        this.setState({
            [event.target.name]: event.target.value
        });
    }

    handleSubmit(event) {
        const { email, password } = this.state;

        axios
          .get(
            "api/users/login",
            {
              user: {
                email: email,
                password: password,
              },
            },
            { withCredentials: true }
          )
          .then((response) => {
            console.log("res from login", response);
            //   if (response.data.logged_in) {
            //     this.props.handleSuccessfulAuth(response.data);
            //   }
          })
          .catch((error) => {
            console.log("login error", error);
          });
            event.preventDefault();
    }

        render(){
           return (
               <div>
                   <form>
                       <input
                         type = "email"
                         name = "email"
                         placeholder = "Email"
                         value = { this.state.email }
                         onChange = { this.handleChange }
                         required
                       />

                       <input
                         type = "password"
                         name = "password"
                         placeholder = "Password"
                         value = { this.state.password }
                         onChange = { this.handleChange }
                         required
                       /> 

                       {/* <button type = "submit">Login</button> */}
                       <input className={"btn btn-primary" + !this.state.validation.isComplete ? "disabled" : "" } disabled={!this.state.validation.isComplete ? true : false } type="submit" onClick={this.handleSubmit} value="submit"/>
                   </form>
               </div>
           ); 
        }

}
