import React, { Component } from "react";
import axios from "axios";

export default class Registration extends Component {
    constructor(props) {
        super(props);

        this.state = {
            email: "",
            password: "",
            password_confirmation: "",
            registration: {
                success: false,
                log: ""
            }
        }
        this.handleSubmit = this.handleSubmit.bind(this);
        this.handleChange = this.handleChange.bind(this);
    }

    handleChange(event) {
        // console.log("handle change", event);
        this.setState({
            [event.target.name]: event.target.value
        })
    }

    handleSubmit(event) {
        event.preventDefault();
        console.log("form submitted");

        const {
            email,
            password,
        } = this.state;

        axios.post("api/users/register",{
            user: {
                email: email,
                password: password,
            }
        },

        { withCredentials: true }

        ).then(response =>{
            const { data } = response
            console.log("registration res", response);
            if (data.error) {
                console.log("registration error", data.message);
                this.setState({
                    registration: {
                        success: false,
                        log: data.message
                    }
                })
                throw new Error(`Status Code: ${data.status}`)
            } else {
                this.setState({
                    registration: {
                        success: true,
                        log: `Successfully created a new employee with email: ${data.email}`
                    }
                })
            }
        })

        .catch(err => {
            console.error(`Registration ${err}`);
        })
    }
    render() {
        return(
            <div>
                <form>
                    <input 
                        type = "email" 
                        name = "email" 
                        placeholder = "Email" 
                        value = { this.state.email } 
                        onChange = { this.handleChange } 
                        required 
                    />
                    <input 
                        type = "password" 
                        name = "password" 
                        placeholder = "Password" 
                        value = { this.state.password } 
                        onChange = { this.handleChange } 
                        required 
                    />
                    <input 
                        type = "password" 
                        name = "password_confirmation" 
                        placeholder = "Password confirmation" 
                        value = { this.state.password_confirmation } 
                        onChange = { this.handleChange } 
                        required 
                    />

                    <p style={{color: this.state.registration.success ? "green" : "red"}}>{this.state.registration.log}</p>

                    <input className="btn btn-primary disabled" type="submit" onClick={() => this.handleSubmit} value=""/>
                </form>
            </div>
        );
    }
}
