import React, { useState } from "react";
import axios from "axios";

export default function Registration () {

    const [email, setEmail] = useState("")
    const [password, setPassword] = useState("")
    const [confirmation, setConfirmation] = useState("")
    const [registration, setRegistration] = useState({ success: false, log: "" })
    const [validation, setValidation] = useState({ isEmailValid: false, isPasswordMatching: false, isFormComplete: false })

    const handleValidation = () => {
        // email
        const $email = document.querySelector('#registration_user_email').value.trim()
        const $password = document.querySelector('#registration_user_password').value.trim()
        const $confirmation = document.querySelector('#registration_user_password_confirmation').value.trim()

        // create a condition to check whether email is a valid email using regEx
        const regEmail = /^[^\s@]+@[^\s@]+\.[^\s@]+$/

        setValidation({
            ...validation,
            ['isPasswordMatching']: $password !== "" && $password === $confirmation ? true : false,
            ['isFormComplete']: $email !== "" && regEmail.test($email) && $password !== "" && $password === $confirmation ? true : false 
        })
    }

    const handleChange = (event) => {
        // console.log("handle change", event);

        handleValidation()

        this.setState({
            [event.target.name]: event.target.value
        })

        switch (event.target.name) {
            case 'email':
                setEmail(event.target.value)
            case 'password':
                setPassword(event.target.value)
            case 'confirmation':
                setConfirmation(event.target.value)
        }
    }

    const handleSubmit = (event) => {
        event.preventDefault();
        console.log("form submitted");

        const {
            email,
            password,
        } = this.state;

        axios.post("api/users/register",{
            user: {
                email: email,
                password: password,

            }
        },

        { withCredentials: true }

        ).then(response =>{
            const { message, status, email, error = null } = response.data
            console.log("registration res", response);
            if (error) {
                console.log("registration error", message);
                this.setState({
                    registration: {
                        success: false,
                        log: message
                    }
                })
                throw new Error(`Status Code: ${status}`)
            } else {
                this.setState({
                    registration: {
                        success: true,
                        log: `Successfully created a new employee with email: ${email}`
                    }
                })
            }
        })

        .catch(err => {
            console.error(`Registration ${err}`);
        })
    }
    return(
        <div>
            <form>
                <input 
                    id="registration_user_email"
                    type = "email" 
                    name = "email" 
                    placeholder = "Email" 
                    value = { this.state.email } 
                    onChange = { this.handleChange } 
                    required 
                />
                <input 
                    id="registration_user_password"
                    type = "password" 
                    name = "password" 
                    placeholder = "Password" 
                    value = { this.state.password } 
                    onChange = { this.handleChange } 
                    required 
                />
                <input 
                    id="registration_user_password_confirmation"
                    type = "password" 
                    name = "confirmation" 
                    placeholder = "Password confirmation" 
                    value = { this.state.password_confirmation } 
                    onChange = { this.handleChange } 
                    required 
                />

                <p style={{color: this.state.registration.success ? "green" : "red"}}>{this.state.registration.log}</p>

                <input className={"btn btn-info" + !this.state.validation.isComplete ? "disabled" : "" } disabled={!this.state.validation.isComplete ? true : false } type="submit" onClick={this.handleSubmit} value="submit"/>
            </form>
        </div>
    );
}
