import React, { Component } from "react";
import axios from "axios";

export default class Registration extends Component {
    constructor(props) {
        super(props);

        this.state = {
            email: "",
            password: "",
            validation: {
                email: {
                    isValid: false
                },
                password: {
                    isMatching: false
                },
                isComplete: false
            },
            password_confirmation: "",
            registration: {
                success: false,
                log: ""
            }
        }
        this.handleSubmit = this.handleSubmit.bind(this);
        this.handleChange = this.handleChange.bind(this);
    }

    handleValidation() {
        // email
        const email = document.querySelector('#registration_user_email').value.trim()
        const password = document.querySelector('#registration_user_password').value.trim()
        const confirmation = document.querySelector('#registration_user_password_confirmation').value.trim()

        // create a condition to check whether email is a valid email using regEx
        const regEmail = /^[^\s@]+@[^\s@]+\.[^\s@]+$/

        this.setState({
            validation: {
                password: {
                    isMatching: password !== "" && password === confirmation ? true : false
                },
                isComplete: email !== "" && regEmail.test(email) ? true : false && this.state
            }
        })
    }

    handleChange(event) {
        // console.log("handle change", event);

        this.handleValidation()

        this.setState({
            [event.target.name]: event.target.value
        })
    }

    handleSubmit(event) {
        event.preventDefault();
        console.log("form submitted");

        const {
            email,
            password,
        } = this.state;

        axios.post("api/users/register",{
            user: {
                email: email,
                password: password,

            }
        },

        { withCredentials: true }

        ).then(response =>{
            const { data } = response
            console.log("registration res", response);
            if (data.error) {
                console.log("registration error", data.message);
                this.setState({
                    registration: {
                        success: false,
                        log: data.message
                    }
                })
                throw new Error(`Status Code: ${data.status}`)
            } else {
                this.setState({
                    registration: {
                        success: true,
                        log: `Successfully created a new employee with email: ${data.email}`
                    }
                })
            }
        })

        .catch(err => {
            console.error(`Registration ${err}`);
        })
    }
    render() {
        return(
            <div>
                <form>
                    <input 
                        id="registration_user_email"
                        type = "email" 
                        name = "email" 
                        placeholder = "Email" 
                        value = { this.state.email } 
                        onChange = { this.handleChange } 
                        required 
                    />
                    <input 
                        id="registration_user_password"
                        type = "password" 
                        name = "password" 
                        placeholder = "Password" 
                        value = { this.state.password } 
                        onChange = { this.handleChange } 
                        required 
                    />
                    <input 
                        id="registration_user_password_confirmation"
                        type = "password" 
                        name = "password_confirmation" 
                        placeholder = "Password confirmation" 
                        value = { this.state.password_confirmation } 
                        onChange = { this.handleChange } 
                        required 
                    />

                    <p style={{color: this.state.registration.success ? "green" : "red"}}>{this.state.registration.log}</p>

                    <input className={"btn btn-primary" + !this.state.validation.isComplete ? "disabled" : "" } disabled={!this.state.validation.isComplete ? true : false } type="submit" onClick={this.handleSubmit} value="submit"/>
                </form>
            </div>
        );
    }
}
